const { getGlobals } = require('./index');

/**
 * @param  {...string|Object<string>} filters see index.js documentation
 */
module.exports = (...filters) => {
    const globals = getGlobals(...filters);
    Object.entries(globals).forEach(entry => {
        if(entry){
            const [ name, proxy ] = entry;
            globalThis[name] = proxy;
        }
    });
}